function App() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [groupByCompany, setGroupByCompany] = useState(false);

  useEffect(() => {
    // Check if we're running in Monday.com context
    if (window.location.hostname === 'localhost') {
      // Use mock data for local development
      setLoading(true);
      setTimeout(() => {
        setItems(mockData);
        setLoading(false);
      }, 1500); // Simulate loading time
    } else {
      // Original Monday.com integration
      monday.listen("context", async (res) => {
        try {
          setLoading(true);
          const { itemIds = [], boardId } = res.data;

          if (itemIds.length > 0) {
            const query = `query {
              items(ids: [${itemIds.join(",")}]) {
                id
                name
                group {
                  title
                }
                column_values {
                  id
                  title
                  text
                  value
                  type
                }
              }
            }`;

            const response = await monday.api(query);
            setItems(response.data.items);
          } else {
            setItems([]);
          }
        } catch (err) {
          setError(err.message);
        } finally {
          setLoading(false);
        }
      });
    }
  }, []);

  const getColumnValue = (item, columnId) => {
    const column = item.column_values.find(col => col.id === columnId);
    return column ? column.text : "";
  };

  const getStatusColor = (statusText) => {
    switch (statusText.toLowerCase()) {
      case "done":
        return "#00c875";
      case "working on it":
        return "#fdab3d";
      case "stuck":
        return "#df2f4a";
      default:
        return "#c4c4c4";
    }
  };

  const getTierColor = (tierText) => {
    if (tierText.includes("Tier 1")) return "#00c875";
    if (tierText.includes("Tier 2")) return "#fdab3d";
    if (tierText.includes("Tier 3")) return "#df2f4a";
    return "#c4c4c4";
  };

  const groupItemsByCompany = (items) => {
    const grouped = {};
    items.forEach(item => {
      const company = getColumnValue(item, "text_mkvq302h") || "Unknown Company";
      if (!grouped[company]) {
        grouped[company] = [];
      }
      grouped[company].push(item);
    });
    return grouped;
  };

  const renderItemCard = (item) => {
    const firstName = getColumnValue(item, "text_mkvq79n9");
    const lastName = getColumnValue(item, "text_mkvqmkfq");
    const company = getColumnValue(item, "text_mkvq302h");
    const jobTitle = getColumnValue(item, "text_mkvq6jba");
    const email = getColumnValue(item, "email_mkvqvcpa");
    const phone = getColumnValue(item, "phone_mkvq7tjy");
    const website = getColumnValue(item, "link_mkvq4c9k");
    const linkedin = getColumnValue(item, "link_mkvqa21x");
    const industry = getColumnValue(item, "color_mkvq9mn1");
    const tier = getColumnValue(item, "color_mkvq2hen");
    const status = getColumnValue(item, "color_mkvx9ayq");
    const engagementScore = getColumnValue(item, "numeric_mkvq46h0");
    const nextAction = getColumnValue(item, "color_mkvs4gw8");
    const nextActionDate = getColumnValue(item, "date_mkvqv4ex");
    const painPoints = getColumnValue(item, "long_text_mkvqqt5j");

    return (
      <div key={item.id} className="lead-card">
        <div className="lead-header">
          <div className="lead-name">
            <h3>{firstName} {lastName}</h3>
            <p className="job-title">{jobTitle}</p>
          </div>
          <div className="lead-status">
            <span 
              className="status-badge"
              style={{ backgroundColor: getStatusColor(status) }}
            >
              {status}
            </span>
            <span 
              className="tier-badge"
              style={{ backgroundColor: getTierColor(tier) }}
            >
              {tier}
            </span>
          </div>
        </div>

        <div className="company-info">
          <h4>🏢 {company}</h4>
          <p className="industry">📊 {industry}</p>
        </div>

        <div className="contact-info">
          <div className="contact-item">
            <span className="icon">📧</span>
            <a href={`mailto:${email}`}>{email}</a>
          </div>
          <div className="contact-item">
            <span className="icon">📞</span>
            <a href={`tel:${phone}`}>{phone}</a>
          </div>
          {website && (
            <div className="contact-item">
              <span className="icon">🌐</span>
              <a href={website} target="_blank" rel="noopener noreferrer">
                Website
              </a>
            </div>
          )}
          {linkedin && (
            <div className="contact-item">
              <span className="icon">💼</span>
              <a href={linkedin} target="_blank" rel="noopener noreferrer">
                LinkedIn
              </a>
            </div>
          )}
        </div>

        <div className="engagement-metrics">
          <div className="metric">
            <span className="metric-label">Engagement Score</span>
            <span className="metric-value">{engagementScore}/100</span>
          </div>
          <div className="progress-bar">
            <div 
              className="progress-fill"
              style={{ width: `${engagementScore}%` }}
            ></div>
          </div>
        </div>

        {nextAction && (
          <div className="next-action">
            <h5>🎯 Next Action</h5>
            <p>{nextAction}</p>
            {nextActionDate && <p className="due-date">Due: {nextActionDate}</p>}
          </div>
        )}

        {painPoints && (
          <div className="pain-points">
            <h5>⚠️ Pain Points</h5>
            <p>{painPoints}</p>
          </div>
        )}

        <div className="pipeline-stage">
          <span className="stage-badge">{item.group?.title || "Unknown Stage"}</span>
        </div>
      </div>
    );
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading sales pipeline data...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <h2>❌ Error Loading Data</h2>
        <p>{error}</p>
      </div>
    );
  }

  if (items.length === 0) {
    return (
      <div className="empty-container">
        <h2>📋 No Items Selected</h2>
        <p>Select one or more items from the board to view their details.</p>
      </div>
    );
  }

  const groupedItems = groupByCompany ? groupItemsByCompany(items) : { "All Items": items };

  return (
    <div className="app">
      <div className="app-header">
        <h1>🚀 Pronti Sales Pipeline</h1>
        <div className="controls">
          <button 
            className={`toggle-btn ${groupByCompany ? 'active' : ''}`}
            onClick={() => setGroupByCompany(!groupByCompany)}
          >
            {groupByCompany ? '📊 Show All' : '🏢 Group by Company'}
          </button>
          <span className="item-count">{items.length} item{items.length !== 1 ? 's' : ''} selected</span>
        </div>
      </div>

      <div className="items-container">
        {Object.entries(groupedItems).map(([company, companyItems]) => (
          <div key={company} className="company-group">
            {groupByCompany && <h2 className="company-title">🏢 {company}</h2>}
            <div className="leads-grid">
              {companyItems.map(renderItemCard)}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

